import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Dashboard.css';

function Dashboard({ setCurrentRoom, setUsername }) {
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [newRoomName, setNewRoomName] = useState('');
  const [createUsername, setCreateUsername] = useState('');
  const [joinUsername, setJoinUsername] = useState('');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showJoinModal, setShowJoinModal] = useState(false);
  const [selectedRoom, setSelectedRoom] = useState(null);
  
  const navigate = useNavigate();

  // Fetch available rooms
  useEffect(() => {
    fetchRooms();
  }, []);

  const fetchRooms = async () => {
    setLoading(true);
    setError(null);
    try {
      // In a real app, this would be an API call to get rooms
      const response = await fetch('/api/rooms');
      
      if (!response.ok) {
        throw new Error(`Server returned ${response.status}: ${response.statusText}`);
      }
      
      const data = await response.json();
      setRooms(data.rooms);
    } catch (err) {
      console.error('Error fetching rooms:', err);
      setError('Failed to load rooms. Please try again later.');
      // For demo, create dummy data
      setRooms([
        { id: '1', name: 'General Chat', participants: 5 },
        { id: '2', name: 'Tech Discussion', participants: 3 },
        { id: '3', name: 'Coffee Break', participants: 2 },
        { id: '4', name: 'Project Alpha', participants: 8 },
        { id: '5', name: 'Random Talk', participants: 4 },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateRoom = async () => {
    if (!newRoomName || !createUsername) return;
    
    setLoading(true);
    try {
      // In a real app, this would be an API call to create a room
      const response = await fetch('/create', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`Server returned ${response.status}: ${response.statusText}`);
      }
      
      const roomId = await response.text();
      
      // Update the room in the parent component
      const newRoom = {
        id: roomId,
        name: newRoomName,
        participants: 1
      };
      
      setCurrentRoom(newRoom);
      setUsername(createUsername);
      
      // Navigate to the chat page
      navigate(`/chat/${roomId}`);
    } catch (err) {
      console.error('Error creating room:', err);
      setError('Failed to create room. Please try again.');
      
      // For demo, create a dummy room and navigate
      const dummyRoomId = `room-${Date.now()}`;
      const dummyRoom = {
        id: dummyRoomId,
        name: newRoomName,
        participants: 1
      };
      
      setCurrentRoom(dummyRoom);
      setUsername(createUsername);
      navigate(`/chat/${dummyRoomId}`);
    } finally {
      setLoading(false);
      setShowCreateModal(false);
    }
  };

  const openJoinModal = (room) => {
    setSelectedRoom(room);
    setShowJoinModal(true);
  };

  const handleJoinRoom = () => {
    if (!selectedRoom || !joinUsername) return;
    
    setCurrentRoom(selectedRoom);
    setUsername(joinUsername);
    navigate(`/chat/${selectedRoom.id}`);
    setShowJoinModal(false);
  };

  // Group rooms in rows of 3
  const groupedRooms = [];
  for (let i = 0; i < rooms.length; i += 3) {
    groupedRooms.push(rooms.slice(i, i + 3));
  }

  return (
    <div className="room-listing-page">
      <header>
        <h1>Chat Rooms</h1>
        <button 
          className="create-room-btn"
          onClick={() => setShowCreateModal(true)}
          disabled={loading}
        >
          Create New Room
        </button>
      </header>

      {error && <div className="error-message">{error}</div>}

      {loading ? (
        <div className="loading">Loading rooms...</div>
      ) : (
        <div className="rooms-grid">
          {groupedRooms.map((row, rowIndex) => (
            <div key={rowIndex} className="room-row">
              {row.map(room => (
                <div key={room.id} className="room-card">
                  <h3>{room.name}</h3>
                  <p>{room.participants} participants online</p>
                  <button 
                    className="join-btn"
                    onClick={() => openJoinModal(room)}
                  >
                    Join Room
                  </button>
                </div>
              ))}
            </div>
          ))}
        </div>
      )}

      {/* Create Room Modal */}
      {showCreateModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Create New Room</h2>
            <div className="form-group">
              <label>Room Name:</label>
              <input
                type="text"
                value={newRoomName}
                onChange={(e) => setNewRoomName(e.target.value)}
                placeholder="Enter room name"
              />
            </div>
            <div className="form-group">
              <label>Your Name:</label>
              <input
                type="text"
                value={createUsername}
                onChange={(e) => setCreateUsername(e.target.value)}
                placeholder="Enter your name"
              />
            </div>
            <div className="modal-actions">
              <button onClick={() => setShowCreateModal(false)}>Cancel</button>
              <button 
                onClick={handleCreateRoom}
                disabled={!newRoomName || !createUsername || loading}
              >
                Create Room
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Join Room Modal */}
      {showJoinModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Join {selectedRoom?.name}</h2>
            <div className="form-group">
              <label>Your Name:</label>
              <input
                type="text"
                value={joinUsername}
                onChange={(e) => setJoinUsername(e.target.value)}
                placeholder="Enter your name"
              />
            </div>
            <div className="modal-actions">
              <button onClick={() => setShowJoinModal(false)}>Cancel</button>
              <button 
                onClick={handleJoinRoom}
                disabled={!joinUsername}
              >
                Join Chat
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
